    &lt;span class=&#34;hljs-function&#34;&gt;T &lt;span class=&#34;hljs-title&#34;&gt;min&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(T x, T y)&lt;/span&gt;
