    &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; 
        R[p] = &lt;span class=&#34;hljs-built_in&#34;&gt;modify&lt;/span&gt;(R[p], mid + &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;, r, x, val);
