            x = x * &lt;span class=&#34;hljs-number&#34;&gt;10&lt;/span&gt; + ch - &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;0&amp;#x27;&lt;/span&gt;, ch = &lt;span class=&#34;hljs-built_in&#34;&gt;getchar&lt;/span&gt;();
