        &amp;#123;
            &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (ch == &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;-&amp;#x27;&lt;/span&gt;)
