    &lt;/span&gt;&amp;#123;
        &lt;span class=&#34;hljs-built_in&#34;&gt;access&lt;/span&gt;(x);
        &lt;span class=&#34;hljs-built_in&#34;&gt;pushrev&lt;/span&gt;(x);
