    &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (ans == INF)
        &lt;span class=&#34;hljs-built_in&#34;&gt;puts&lt;/span&gt;(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;-1&amp;quot;&lt;/span&gt;);
