        &amp;#123;
            x = -x;
            &lt;span class=&#34;hljs-built_in&#34;&gt;putchar&lt;/span&gt;(&lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;-&amp;#x27;&lt;/span&gt;);
