        &lt;span class=&#34;hljs-built_in&#34;&gt;spfa&lt;/span&gt;();

        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (d[n] != INF)
            ans = &lt;span class=&#34;hljs-built_in&#34;&gt;min&lt;/span&gt;(ans, g[i].a + d[n]);
