            &amp;#123;
                h[v] = h[u] + e[i].c;
                &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (!st[v])
                &amp;#123;
                    q.&lt;span class=&#34;hljs-built_in&#34;&gt;push&lt;/span&gt;(v);
